  `user_birthday` varchar(255) NOT NULL,
  `user_address` varchar(255) NOT NULL,
  `user_createdTime` datetime NOT NULL COMMENT '用户注册时间',
  `user_isFreeze` tinyint(1) NOT NULL DEFAULT '0' COMMENT '用户是否被冻结',
  PRIMARY KEY (`user_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

